import {Component} from "react";
import {Journey} from "./Journey";
import {Cognate} from "./Cognate";

export class Collections extends Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			collectionType: this.props.mapMode,
			items: [
				{   // TODO: Dummy data
					header: {word: "horse", language: "English (UK)"},
					childNodes: [{word: "kers", language: "Proto-Indo-European"}, {word: "krsos", language: "Proto-Indo-European"},
						{word: "hrussa", language: "Proto-Germanic"}, {word: "hross", language: "Proto-West-Germanic"}, {word: "horse", language: "English"}]
				}
			]
		};
	}

	render()
	{
		let itemElements = [];

		this.state.items.map((item, index) => {
			if(this.state.collectionType === "cognate")
			{
				itemElements.push(
					<Cognate />
				)
			}
			else if(this.state.collectionType === "journey")
			{
				itemElements.push(
					<Journey header={item.header} childNodes={item.childNodes} />
				)
			}

		})

		return(
			<div>
				{itemElements}
			</div>
		)
	}
}
