import {Component} from "react";
import {Textbox} from "../generic/Textbox";
import {LabeledControl} from "./LabeledControl";
import {ColourSelector2} from "../generic/ColourSelector2";

export class LabeledColourSelector extends Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			hint: this.props.hint || "Hexadecimal value (e.g. #000000)"
		};

		this.changeColourTimeout = null; // For throttling colour selector's onChange call rate
	}

	render()
	{
		return(
			<LabeledControl label={this.props.label}>
				<Textbox name={this.props.name} value={this.props.value} hint={this.state.hint} onChange={this.props.onChange} />
				<ColourSelector2 name={this.props.name} value={this.props.value} onChange={this.props.onChange} />
			</LabeledControl>
		)
	}
}
