import {Component} from "react";
import {Textbox} from "./Textbox";

export class LabeledColourSelector extends Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			hint: this.props.hint || "Hexadecimal value (e.g. #000000)"
		};

		this.changeColourTimeout = null; // For throttling the colour selector
	}

	render()
	{
		return(
			<div className={"labeled-control"}>
				<div className={"label"}>{this.props.label} </div>
				<div className={"control"}>
					<Textbox name={this.props.name} value={this.props.value} hint={this.state.hint} onChange={this.props.onChange} />
					<input name={this.props.name} type={"color"} defaultValue={this.props.value} onChange={(e) =>
					{
						// Throttle the change
						const component = this;

						window.clearTimeout(this.changeColourTimeout);
						this.changeColourTimeout = window.setTimeout(function()
						{
							component.props.onChange(e);
						}, 100);
					}} />
				</div>
			</div>
		)
	}
}
