import {Component} from "react";
import {JourneyHeader} from "./JourneyHeader";
import {JourneyNode} from "./JourneyNode";
import {Meatballs} from "./Meatballs";
import {Collapser} from "./Collapser";

export class Journey extends Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			header: this.props.header || {},
			childNodes: this.props.childNodes || [],
			index: this.props.index,
			collapsed: false
		};

		this.collapseCollection = this.collapseCollection.bind(this);
	}

	collapseCollection(e)
	{
		this.setState({collapsed: true});
	}

	render()
	{
		let childNodeElements = [];

		this.state.childNodes.forEach((childNode) => {
			childNodeElements.push(<JourneyNode word={childNode.word} />);
		});

		return(
			<div className={"journey-container"}>
				<div className={"journey-header"}>
					<h2>{this.state.header.word}</h2>
					<h2>{this.state.header.language}</h2>
					<Meatballs />
					<Collapser collapseCollection={this.collapseCollection} />
				</div>
				{childNodeElements}
			</div>
		)
	}
}
